// Translation functions

function getLanguage() {
  document.getElementById("bLang").innerHTML = navigator.language.substring(0, 2);
  setLang(navigator.language.substring(0, 2));
}

let langs = ['en', 'de', 'es'];
let lang = 'en';
setLangStyles(lang);
var bLang = navigator.language.substring(0, 2);
getLanguage();
translatedStrings(bLang);

function setStyles(styles) {
  var elementId = '__lang_styles';
  var element = document.getElementById(elementId);
  if (element) {
    element.remove();
  }

  let style = document.createElement('style');
  style.id = elementId;
  style.type = 'text/css';

  if (style.styleSheet) {
    style.styleSheet.cssText = styles;
  } else {
    style.appendChild(document.createTextNode(styles));
  }
  document.getElementsByTagName('head')[0].appendChild(style);
}

function setLang(lang) {
  setLangStyles(lang);
}

function setLangStyles(lang) {
  let styles = langs
    .filter(function (l) {
      return l != lang;
    })
    .map(function (l) {
      return ':lang(' + l + ') { display: none; }';
    })
    .join(' ');

  setStyles(styles);
}

